function togglePanel(e,t){const l=document.querySelector(t),n=document.querySelector(e);l.addEventListener("click",e=>{e.preventDefault(),n.style.display="flex",document.body.style.overflow="hidden",n.addEventListener("click",e=>{e.target===n&&(n.style.display="none",document.body.style.overflow="")})})}togglePanel(".panel",".hamburger");